        "$:/plugins/twpub/0392ce071f7ef821/text/000000015": {
            "role": "text",
            "title": "$:/plugins/twpub/0392ce071f7ef821/text/000000015",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-0392ce071f7ef821-7763c0 twpub-0392ce071f7ef821-e1e343 twpub-0392ce071f7ef821-6b4cd0\"><table class=\"c10\">\n<tbody><tr>\n<td><$link class=\"c9 pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000017\">WALDEN</$link><br>\n<br></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000020\">Economy</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000290\">Where I Lived, and What I Lived For</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000345\">Reading</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000372\">Sounds</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000429\">Solitude</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000470\">Visitors</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000527\">The Bean-Field</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000572\">The Village</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000585\">The Ponds</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000658\">Baker Farm</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000689\">Higher Laws</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000728\">Brute Neighbors</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000767\">House-Warming</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000812\">Former Inhabitants and Winter Visitors</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000867\">Winter Animals</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000900\">The Pond in Winter</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000000951\">Spring</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000001010\">Conclusion</$link><br>\n<br></td>\n</tr>\n<tr>\n<td><$link class=\"c9 pginternal\" to=\"$:/plugins/twpub/0392ce071f7ef821/text/000001063\">ON THE DUTY OF CIVIL DISOBEDIENCE</$link></td>\n</tr>\n</tbody></table></div>"
        },
