            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-0392ce071f7ef821-7763c0 twpub-0392ce071f7ef821-e1e343 twpub-0392ce071f7ef821-6b4cd0\"><div class=\"chapter\"><h5>THE END</h5></div></div>"
        },
        "$:/plugins/twpub/0392ce071f7ef821/text/000001061": {
            "role": "text",
