        "$:/plugins/twpub/0fa7661de48f2362/text/000000008": {
            "role": "text",
            "title": "$:/plugins/twpub/0fa7661de48f2362/text/000000008",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-0fa7661de48f2362-f09fcd twpub-0fa7661de48f2362-6b4cd0\"><p style=\"\">Produced by Shiou-Yun Lin</p></div>"
