        },
        "$:/plugins/twpub/112af7d90f46baad/text/000000914": {
            "role": "text",
            "title": "$:/plugins/twpub/112af7d90f46baad/text/000000914",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-112af7d90f46baad-58df80 twpub-112af7d90f46baad-25d4bc twpub-112af7d90f46baad-6b4cd0\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/112af7d90f46baad/text/000000527\"><span class=\"label\">[23]</span></$link> P. 108. The play referred to is supposed to be &quot;Le Comte d'Essex,&quot; by Thomas Corneille.</p></div></div>"
        },
        "$:/plugins/twpub/112af7d90f46baad/text/000000915": {
            "role": "text",
            "title": "$:/plugins/twpub/112af7d90f46baad/text/000000915",
