        "$:/plugins/twpub/112af7d90f46baad/text/000000923": {
            "role": "text",
            "title": "$:/plugins/twpub/112af7d90f46baad/text/000000923",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-112af7d90f46baad-58df80 twpub-112af7d90f46baad-25d4bc twpub-112af7d90f46baad-6b4cd0\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/112af7d90f46baad/text/000000550\"><span class=\"label\">[26]</span></$link> P. 111. Gabriel Gauchat (1709-1779), French ecclesiastical writer, was author of a number of works on religious subjects.</p></div></div>"
        },
