        "$:/plugins/twpub/17f7f7f28f29cca2/text/000000008": {
            "role": "text",
            "title": "$:/plugins/twpub/17f7f7f28f29cca2/text/000000008",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-17f7f7f28f29cca2-f09fcd twpub-17f7f7f28f29cca2-6b4cd0\"><p style=\"\">Produced by Huang Chang Shiang</p></div>"
