        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000332": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000332",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"> </p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000333": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000333",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000334": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000334",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal c10\"><span class=\"c9\">陰</span> “yin” : the feminine principle; receptive, yielding, dark, cool, soft</p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000335": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000335",
