        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000338": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000338",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal c10\"><span class=\"c9\">陽</span> “yang” : the masculine principle; aggressive, active, light, warm, hard</p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000339": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000339",
