        "$:/plugins/twpub/1a2844f534b920c9/text/000000354": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000354",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal c10\"><span class=\"c9\">谷</span> “valley” : the image of the valley is often used in the DDJ; a valley represents receptiveness (because it accepts all things that come into it), openness, and emptiness, and so is feminine in nature</p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000355": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000355",
