        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000358": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000358",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal c10\"><span class=\"c9\">身</span> “self” : often translated as “body” by others (it can mean either), it is my personal bias that this symbol seems to consistently refer to the sense-of-self, personality, or ego</p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000000359": {
