        "$:/plugins/twpub/1a2844f534b920c9/text/000000378": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000378",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal c10\"><span class=\"c9\">百</span> <span></span><span class=\"c9\">姓</span> <span></span>“100 families” : in ancient times this phrase referred to the entire ruling class</p></div></div>"
        },
