        "$:/plugins/twpub/1a2844f534b920c9/text/000000382": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000000382",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal c10\"><span class=\"c9\">士</span> “scholar” : around the time of the DDJ, this referred to the elite “intellectual” class of  Chinese society, the teachers and philosophers (Confucius would have been considered a scholar)</p></div></div>"
        },
