            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c5\"><span class=\"c24\">Seven</span></span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001365": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001365",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001366": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001366",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Heaven is eternal, earth is enduring.                           </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001367": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001367",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001368": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001368",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The reason that heaven and earth can be eternal and</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001369": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001369",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001370": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001370",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      enduring</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001371": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001371",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001372": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001372",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Is because they do not live for themselves.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001373": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001373",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001374": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001374",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Therefore they can live forever.         </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001375": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001375",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
