            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001376",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001377": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001377",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001378": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001378",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Thus the sage :</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001379": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001379",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001380": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001380",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Puts his self behind others, yet finds his self before them.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001381": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001381",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001382": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001382",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Considers his self extraneous, yet his self survives.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001383": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001383",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
