            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">This one thing :</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001537": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001537",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001538": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001538",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Its highest point is not bright;</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001539": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001539",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001540": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001540",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Its lowest point is not dark.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001541": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001541",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001542": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001542",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Continuous and unending!, it can not be named;</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001543": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001543",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001544": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001544",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">It returns to non-existence.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001545": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001545",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001546": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001546",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">It is called the form of that which is without-form;</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001547": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001547",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001548": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001548",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The image of non-existence.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001549": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001549",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001550": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001550",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">It is called confusing and indistinct.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001551": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001551",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001552": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001552",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Meet it and you do not see its beginning;</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001553": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001553",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001554": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001554",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Follow it and you do not see its end.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001555": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001555",
