            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001558",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Hold fast to the Way of the ancients</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001559": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001559",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001560": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001560",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">In order to master the present moment.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001561": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001561",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001562": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001562",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The ability to know the ancient beginning –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001563": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001563",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001564": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001564",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">This is called the main principle of Dao.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001565": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001565",
