        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001677": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001677",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001678": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001678",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">“Yes” together with “yeah” –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001679": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001679",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001680": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001680",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">What is their mutual distance or nearness?</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001681": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001681",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001682": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001682",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Beautiful together with ugliness –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001683": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001683",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001684": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001684",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">What is their mutual distance or similarity?</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001685": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001685",
