            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001687": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001687",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001688": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001688",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">That which people fear, they are not able to not fear.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001689": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001689",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001690": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001690",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">How ridiculous! They are not yet centered!</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001691": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001691",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001692": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001692",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001693": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001693",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001694": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001694",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Everyone is very festive,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001695": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001695",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001696": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001696",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">As if enjoying the Tai Lao sacrifice,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001697": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001697",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001698": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001698",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">As if climbing terraces in the spring.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001699": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001699",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001700": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001700",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">I alone am unmoved! –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001701": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001701",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001702": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001702",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Like one who has not yet given any sign,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001703": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001703",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001704": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001704",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Like a newborn infant who does not yet act like a baby.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001705": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001705",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001706": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001706",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Very tired and worn out!, as if without a place to return</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001707": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001707",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001708": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001708",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      to.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001709": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001709",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001710": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001710",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001711": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001711",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001712": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001712",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Everyone all has more than they need;</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001713": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001713",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001714": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001714",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">I alone seem to have lost everything.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001715": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001715",
            "type": "text/vnd.twpub",
