        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001859": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001859",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001860": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001860",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Something existed unformed yet complete,<br>\nBefore heaven and earth were created.<br>\nSilent!  Empty!</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001861": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001861",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001862": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001862",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Standing alone, not changing.<br>\nIt circulates everywhere, and causes no danger.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001863": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001863",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001864": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001864",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">It can be considered the mother of the world.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001865": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001865",
