        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001867": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001867",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001868": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001868",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">I do not know its name;</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001869": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001869",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001870": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001870",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Its symbol is called Dao.<br>\nIf I tried to make its name, I would call it great.<br>\nBeing great speaks of departing.<br>\nDeparting speaks of being remote.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001871": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001871",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001872": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001872",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Being remote speaks of returning.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001873": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001873",
            "type": "text/vnd.twpub",
