        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001875": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001875",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001876": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001876",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Dao is great,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001877": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001877",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001878": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001878",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Heaven is great,<br>\nEarth is great,<br>\nThe king is also great.<br>\nWithin the realm exist four that are great,<br>\nAnd the king resides as one of them!</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001879": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001879",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
