            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001880",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001881": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001881",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001882": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001882",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">People follow the earth.<br>\nThe earth follows heaven.<br>\nHeaven follows Dao.<br>\nDao follows what is naturally so.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001883": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001883",
