            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001890": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001890",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Know your maleness, but maintain your femaleness.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001891": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001891",
