        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001892": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001892",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Serve as a stream to the world.   </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001893": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001893",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001894": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001894",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Serving as a stream to the world,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001895": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001895",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000001896": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000001896",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The ever-constant De will not depart,</span></p></div></div>"
