            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The world had a beginning,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002417": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002417",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002418": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002418",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Which can be considered the mother of the world.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002419": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002419",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002420": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002420",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Once you have realized its mother,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002421": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002421",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002422": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002422",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">You thus know her children.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002423": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002423",
