        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002424": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002424",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Once you know her children,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002425": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002425",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002426": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002426",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Return to and maintain their mother.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002427": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002427",
