        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002433": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002433",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002434": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002434",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Block your senses, close your gateway –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002435": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002435",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002436": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002436",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">All your life you will not struggle.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002437": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002437",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002438": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002438",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Open your senses, be successful in your duties –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002439": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002439",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002440": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002440",
            "type": "text/vnd.twpub",
