            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      without-knowledge and without-desire,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002577": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002577",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002578": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002578",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Those who make men wise will not dare to act.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002579": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002579",
