            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">What is hollow then becomes filled.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002759": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002759",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002760": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002760",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">What is worn out then becomes new.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002761": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002761",
