        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002783": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002783",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002784": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002784",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Does not brag about himself, therefore he endures.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002785": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002785",
