        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002788": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002788",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Now : only because he does not strive,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002789": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002789",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002790": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002790",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Therefore no one in the world can strive against him.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002791": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002791",
