        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002794": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002794",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">That which the ancients say :</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002795": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002795",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002796": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002796",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">“He who is wrong then becomes whole and perfect” –</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002797": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002797",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002798": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002798",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">How can these be empty words?</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002799": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002799",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000002800": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000002800",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Be truly whole and perfect, and return to it.</span></p></div></div>"
        },
