        "$:/plugins/twpub/1a2844f534b920c9/text/000004192": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004192",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">When your courage lies in daring,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004193": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004193",
            "type": "text/vnd.twpub",
