        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004194": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004194",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The consequence is killing.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004195": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004195",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004196": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004196",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">When your courage lies in not daring,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004197": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004197",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004198": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004198",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The consequence is survival.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004199": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004199",
