        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004202": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004202",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">These two choices, they sometimes cause benefit,</span></p></div></div>"
        },
