        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004303": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004303",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004304": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004304",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The people are born (indeed!) soft and weak.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004305": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004305",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004306": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004306",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">They die (indeed!) hard and inflexible.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004307": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004307",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004308": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004308",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">The ten thousand creatures, the grass and trees, are born</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004309": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004309",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004310": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004310",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      (indeed!) soft and fragile.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004311": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004311",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004312": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004312",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">They die (indeed!) dried and withered.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004313": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004313",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
