        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004315": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004315",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004316": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004316",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Therefore : that which is hard and inflexible is a follower</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004317": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004317",
            "type": "text/vnd.twpub",
