        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004318": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004318",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      of death.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004319": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004319",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004320": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004320",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">That which is soft and weak is a follower of life.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004321": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004321",
            "type": "text/vnd.twpub",
