            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      wealthy.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004749": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004749",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004750": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004750",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">I am without-desire, and the citizens naturally return to</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004751": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004751",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004752": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004752",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">      the state of the uncarved block.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004753": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004753",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004754": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004754",
            "type": "text/vnd.twpub",
