        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004755": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004755",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004756": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004756",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Thus the sage :</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004757": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004757",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004758": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004758",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Is honest and does not divide,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004759": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004759",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004760": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004760",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Is honorable and does not injure,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004761": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004761",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004762": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004762",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Is straightforward and does not indulge in excess,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004763": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004763",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004764": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004764",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Is bright but does not dazzle.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004765": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004765",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
