        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004772": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004772",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Thus the sage in the end does not act great,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004773": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004773",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004774": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004774",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Therefore he can achieve his greatness.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004775": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004775",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004776": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004776",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004777": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004777",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004778": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004778",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Thus the sage plans for things to be difficult,</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004779": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004779",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004780": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004780",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Therefore in the end he is without difficulty!</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004781": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004781",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004782": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004782",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004783": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004783",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004784": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004784",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Thus the sage :</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004785": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004785",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004786": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004786",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Does not act, therefore he does not ruin.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004787": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004787",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004788": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004788",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\">Does not grasp, therefore he does not lose.</span></p></div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004789": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004789",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\">\n</div></div>"
        },
        "$:/plugins/twpub/1a2844f534b920c9/text/000004790": {
            "role": "text",
            "title": "$:/plugins/twpub/1a2844f534b920c9/text/000004790",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1a2844f534b920c9-8b1105 twpub-1a2844f534b920c9-65f006 twpub-1a2844f534b920c9-6b4cd0\"><div class=\"WordSection1\"><p class=\"MsoNormal\"><span class=\"c24\"> </span></p></div></div>"
