        "$:/plugins/twpub/1ec6a0584d1b40ed/text/000000018": {
            "role": "text",
            "title": "$:/plugins/twpub/1ec6a0584d1b40ed/text/000000018",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-1ec6a0584d1b40ed-fdf265 twpub-1ec6a0584d1b40ed-56aed2 twpub-1ec6a0584d1b40ed-6b4cd0\"><table>\n<tbody><tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000021\">PREFACE</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000054\">LETTER FROM WENDELL PHILLIPS, ESQ.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000081\">FREDERICK DOUGLASS.</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000086\">CHAPTER I</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000109\">CHAPTER II</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000136\">CHAPTER III</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000151\">CHAPTER IV</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000174\">CHAPTER V</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000201\">CHAPTER VI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000212\">CHAPTER VII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000231\">CHAPTER VIII</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000256\">CHAPTER IX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000271\">CHAPTER X</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000374\">CHAPTER XI</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000421\">APPENDIX</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/1ec6a0584d1b40ed/text/000000435\">A PARODY</$link></td>\n</tr>\n</tbody></table></div>"
