        },
        "$:/plugins/twpub/2c6f1fdf6e479635/text/000000766": {
            "role": "text",
            "title": "$:/plugins/twpub/2c6f1fdf6e479635/text/000000766",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-2c6f1fdf6e479635-b4409c twpub-2c6f1fdf6e479635-25cf14 twpub-2c6f1fdf6e479635-6b4cd0\"><div class=\"c2\">Project Gutenberg™ is synonymous with the free distribution of electronic works in formats readable by the widest variety of computers including obsolete, old, middle-aged and new computers. It exists because of the efforts of hundreds of volunteers and donations from people in all walks of life.</div></div>"
        },
        "$:/plugins/twpub/2c6f1fdf6e479635/text/000000767": {
