        "$:/plugins/twpub/322d77430a929152/text/000000070": {
            "role": "text",
            "title": "$:/plugins/twpub/322d77430a929152/text/000000070",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-322d77430a929152-941138 twpub-322d77430a929152-ae0f97\"><p class=\"noindent\">\n<span class=\"dropcap\">A</span><span class=\"small-caps\"> Pierre Lafitte<br class=\"br\"></span>\n<span class=\"eme\">Mon cher ami,</span><br class=\"br\">\n<span class=\"eme\">Tu m’as engag</span><span class=\"eme\">é sur une route o</span><span class=\"eme\">ù je ne croyais point que je dusse</span> <span class=\"eme\">jamais m’aventurer, et j’y ai trouv</span><span class=\"eme\">é tant de plaisir et d’agr</span><span class=\"eme\">ément</span> <span class=\"eme\">litt</span><span class=\"eme\">éraire qu’il me para</span><span class=\"eme\">ît juste d’inscrire ton nom en t</span><span class=\"eme\">ête de ce</span> <span class=\"eme\">premier volume, et de t’affirmer ici mes sentiments d’affectueuse et</span> <span class=\"eme\">fid</span><span class=\"eme\">èle reconnaissance.</span>\n</p></div>"
        },
