        "$:/plugins/twpub/3cb6b9d5e88177c1/text/000005227": {
            "role": "text",
            "title": "$:/plugins/twpub/3cb6b9d5e88177c1/text/000005227",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-3cb6b9d5e88177c1-fb4c94 twpub-3cb6b9d5e88177c1-a7aea7 twpub-3cb6b9d5e88177c1-6b4cd0\"><div class=\"c16\"><div class=\"c2\">Project Gutenberg™ depends upon and cannot survive without widespread public support and donations to carry out its mission of increasing the number of public domain and licensed works that can be freely distributed in machine-readable form accessible by the widest array of equipment including outdated equipment. Many small donations ($1 to $5,000) are particularly important to maintaining tax exempt status with the IRS.</div></div></div>"
        },
        "$:/plugins/twpub/3cb6b9d5e88177c1/text/000005228": {
