            "text": "<div class=\"twpub-3cb6b9d5e88177c1-fb4c94 twpub-3cb6b9d5e88177c1-a7aea7 twpub-3cb6b9d5e88177c1-6b4cd0\"><div class=\"c16\">\n</div></div>"
        },
        "$:/plugins/twpub/3cb6b9d5e88177c1/text/000005233": {
            "role": "text",
            "title": "$:/plugins/twpub/3cb6b9d5e88177c1/text/000005233",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-3cb6b9d5e88177c1-fb4c94 twpub-3cb6b9d5e88177c1-a7aea7 twpub-3cb6b9d5e88177c1-6b4cd0\"><div class=\"c16\"><div class=\"c2\">International donations are gratefully accepted, but we cannot make any statements concerning tax treatment of donations received from outside the United States. U.S. laws alone swamp our small staff.</div></div></div>"
        },
        "$:/plugins/twpub/3cb6b9d5e88177c1/text/000005234": {
