        "$:/plugins/twpub/50381e1a0c7f0797/text/000000053": {
            "role": "text",
            "title": "$:/plugins/twpub/50381e1a0c7f0797/text/000000053",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-50381e1a0c7f0797-5a33ed twpub-50381e1a0c7f0797-691cf8 twpub-50381e1a0c7f0797-6b4cd0\"><div class=\"book\"><table class=\"illus\">\n\n\n\n<tbody><tr>\n<td> </td>\n<td> </td>\n<td class=\"c11\">Artist.</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000000251\">Marley’s Ghost</$link></p>\n</td>\n<td>      </td>\n<td>J. Leech</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000000406\">Ghosts of Departed Usurers</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000000606\">Mr. Fezziwig’s Ball</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000000725\">Scrooge Extinguishes the First<br>\nof the Three Spirits</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000000751\">Scrooge’s Third Visitor</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000001020\">Ignorance and Want</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000001352\">The Last of the Spirits</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n<tr>\n<td>\n<p class=\"illus\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/50381e1a0c7f0797/text/000001521\">Scrooge and Bob Cratchit</$link></p>\n</td>\n<td> </td>\n<td>,,</td>\n</tr>\n</tbody></table></div></div>"
        },
