        "$:/plugins/twpub/52e1af596b5ab2ae/text/000003434": {
            "role": "text",
            "title": "$:/plugins/twpub/52e1af596b5ab2ae/text/000003434",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-52e1af596b5ab2ae-502ce4 twpub-52e1af596b5ab2ae-a25ea2 twpub-52e1af596b5ab2ae-6b4cd0\"><div class=\"c13\"><div class=\"c2\">Project Gutenberg™ depends upon and cannot survive without widespread public support and donations to carry out its mission of increasing the number of public domain and licensed works that can be freely distributed in machine-readable form accessible by the widest array of equipment including outdated equipment. Many small donations ($1 to $5,000) are particularly important to maintaining tax exempt status with the IRS.</div></div></div>"
        },
        "$:/plugins/twpub/52e1af596b5ab2ae/text/000003435": {
