        "$:/plugins/twpub/56eeb2eb314874fa/text/000000016": {
            "role": "text",
            "title": "$:/plugins/twpub/56eeb2eb314874fa/text/000000016",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-56eeb2eb314874fa-9a7134 twpub-56eeb2eb314874fa-0e3027 twpub-56eeb2eb314874fa-6b4cd0\"><table class=\"c7\">\n<tbody><tr>\n<td>I.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000000018\">A Scandal in Bohemia</$link></td>\n</tr>\n<tr>\n<td>II.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000000539\">The Red-Headed League</$link></td>\n</tr>\n<tr>\n<td>III.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000000972\">A Case of Identity</$link></td>\n</tr>\n<tr>\n<td>IV.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000001249\">The Boscombe Valley Mystery</$link></td>\n</tr>\n<tr>\n<td>V.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000001682\">The Five Orange Pips</$link></td>\n</tr>\n<tr>\n<td>VI.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000002041\">The Man with the Twisted Lip</$link></td>\n</tr>\n<tr>\n<td>VII.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000002488\">The Adventure of the Blue Carbuncle</$link></td>\n</tr>\n<tr>\n<td>VIII.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000002919\">The Adventure of the Speckled Band</$link></td>\n</tr>\n<tr>\n<td>IX.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000003424\">The Adventure of the Engineer’s Thumb</$link></td>\n</tr>\n<tr>\n<td>X.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000003779\">The Adventure of the Noble Bachelor</$link></td>\n</tr>\n<tr>\n<td>XI.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000004234\">The Adventure of the Beryl Coronet</$link></td>\n</tr>\n<tr>\n<td>XII.</td>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/56eeb2eb314874fa/text/000004681\">The Adventure of the Copper Beeches</$link></td>\n</tr>\n</tbody></table></div>"
        },
        "$:/plugins/twpub/56eeb2eb314874fa/text/000000017": {
            "role": "text",
