        },
        "$:/plugins/twpub/65fb807d23063af6/text/000000261": {
            "role": "text",
            "title": "$:/plugins/twpub/65fb807d23063af6/text/000000261",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-65fb807d23063af6-7a2b93 twpub-65fb807d23063af6-de533d twpub-65fb807d23063af6-6b4cd0\"><p class=\"foot\"><span class=\"c3\">110</span> (<$link class=\"pginternal\" to=\"$:/plugins/twpub/65fb807d23063af6/text/000000031\">return</$link>)<br>\n[ I will send for thee at midnightâ In THE HISTORY OF DR. FAUSTUS, we have a particular account of Faustus's visit to the infernal regions, Sig. D 2, ed. 1648.]</p></div>"
        },
        "$:/plugins/twpub/65fb807d23063af6/text/000000262": {
